import React, { useState } from 'react';
import { CSVLink } from 'react-csv';
import './App.css';

const initialData = [
  { id: 1, system: "Email Server", status: "Secure", date: "2025-04-15" },
  { id: 2, system: "Cloud Storage", status: "Vulnerable", date: "2025-04-14" },
  { id: 3, system: "User Database", status: "Secure", date: "2025-04-13" },
  { id: 4, system: "Payment Gateway", status: "Critical", date: "2025-04-10" }
];

function App() {
  const [data, setData] = useState(initialData);
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });

  const sortedData = [...data].sort((a, b) => {
    if (!sortConfig.key) return 0;
    if (a[sortConfig.key] < b[sortConfig.key]) return sortConfig.direction === 'asc' ? -1 : 1;
    if (a[sortConfig.key] > b[sortConfig.key]) return sortConfig.direction === 'asc' ? 1 : -1;
    return 0;
  });

  const handleSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  return (
    <div className="App">
      <h1>Data Leakage Detection System</h1>
      <CSVLink data={data} filename={"data-leakage-report.csv"}>
        <button className="download-btn">Download Report</button>
      </CSVLink>

      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort('id')}>ID</th>
            <th onClick={() => handleSort('system')}>System</th>
            <th onClick={() => handleSort('status')}>Status</th>
            <th onClick={() => handleSort('date')}>Date</th>
          </tr>
        </thead>
        <tbody>
          {sortedData.map((row) => (
            <tr key={row.id}>
              <td>{row.id}</td>
              <td>{row.system}</td>
              <td>{row.status}</td>
              <td>{row.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
