import React, { useRef } from "react";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS } from "chart.js/auto";
import jsPDF from "jspdf";

const LeakChart = () => {
  const chartRef = useRef(null);

  const data = {
    labels: ["April 10", "April 11", "April 12", "April 13", "April 14", "April 15"],
    datasets: [
      {
        label: "Data Leak Alerts",
        data: [5, 10, 7, 3, 8, 4],
        fill: false,
        borderColor: "#2563eb",
        tension: 0.1
      }
    ]
  };

  const exportChartPDF = () => {
    const chartCanvas = chartRef.current?.canvas;
    if (!chartCanvas) return;

    const image = chartCanvas.toDataURL("image/png", 1.0);
    const pdf = new jsPDF();
    pdf.text("Data Leakage Chart Report", 15, 20);
    pdf.addImage(image, "PNG", 15, 30, 180, 90);
    pdf.save("Data_Leakage_Chart_Report.pdf");
  };

  return (
    <div style={{ textAlign: "center", margin: "20px" }}>
      <h2>Leak Alerts Overview</h2>
      <button
        onClick={exportChartPDF}
        style={{
          backgroundColor: "#2563eb",
          color: "white",
          padding: "0.5rem 1rem",
          borderRadius: "8px",
          border: "none",
          cursor: "pointer",
          marginBottom: "10px"
        }}
      >
        Export Chart to PDF
      </button>
      <div style={{ width: "600px", margin: "0 auto" }}>
        <Line ref={chartRef} data={data} />
      </div>
    </div>
  );
};

export default LeakChart;
