import React, { useState, useEffect } from 'react';
import { CSVLink } from 'react-csv';
import LeakChart from './LeakChart';
import './App.css';

function App() {
  const [leaks, setLeaks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate fetching data
    const fetchedData = [
      { leak: "Email Breach", level: "Critical", date: "2025-04-15" },
      { leak: "Password Exposure", level: "High", date: "2025-04-14" },
      { leak: "Public API Data Leak", level: "Medium", date: "2025-04-13" }
    ];
    setTimeout(() => {
      setLeaks(fetchedData);
      setLoading(false);
    }, 1000);
  }, []);

  return (
    <div className="App">
      <h1>Data Leakage Detection System</h1>
      {loading ? (
        <p>Loading data...</p>
      ) : (
        <>
          <CSVLink data={leaks} filename={"data-leak-report.csv"}>
            <button className="download-btn">Download Report (CSV)</button>
          </CSVLink>

          <LeakChart data={leaks} />

          <table>
            <thead>
              <tr>
                <th>Leak Type</th>
                <th>Severity Level</th>
                <th>Date of Breach</th>
              </tr>
            </thead>
            <tbody>
              {leaks.map((leak, index) => (
                <tr key={index}>
                  <td>{leak.leak}</td>
                  <td>{leak.level}</td>
                  <td>{leak.date}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default App;
