import React, { useEffect, useState } from 'react';
import DataLeakTable from './DataLeakTable';
import './App.css';

const simulatedLeaks = [
  { id: 1, leak: 'Sensitive Financial Report', level: 'High', date: '2025-04-10' },
  { id: 2, leak: 'Customer Emails', level: 'Medium', date: '2025-04-12' },
  { id: 3, leak: 'Login Credentials', level: 'Critical', date: '2025-04-15' },
  { id: 4, leak: 'Confidential Strategy', level: 'High', date: '2025-04-14' },
  { id: 5, leak: 'Personal Identifiable Info', level: 'Medium', date: '2025-04-13' },
  { id: 6, leak: 'Source Code', level: 'Critical', date: '2025-04-16' }
];

function App() {
  const [leaks, setLeaks] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setLeaks(simulatedLeaks);
      setLoading(false);
    }, 1500);  // simulate loading delay
  }, []);

  const filteredLeaks = leaks.filter(leak =>
    leak.leak.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="App">
      <h1>üïµÔ∏è‚Äç‚ôÇÔ∏è Company Data Breach Monitor</h1>
      <input
        type="text"
        placeholder="Search for leaks..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="search-input"
      />
      {loading ? <p>Loading data, please wait...</p> : <DataLeakTable leaks={filteredLeaks} />}
    </div>
  );
}

export default App;
